export class UserCreateDto {
  uuid: string;
  userName: string;
  telegramId: string;
  firstName: string;
  lastName: string;

  constructor(init: {
    uuid: string;
    userName: string;
    telegramId: string;
    firstName: string;
    lastName: string;
  }) {
    this.uuid = init.uuid;
    this.userName = init.userName;
    this.telegramId = init.telegramId;
    this.firstName = init.firstName;
    this.lastName = init.lastName;
  }
}